mapper.insert:

  SELECT INQUIRY_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO INQUIRY_T(
    INQUIRY_NO
  , USER_NO
  , TITLE
  , CONTENT
  , INQUIRY_CREATED_AT
  , INQUIRY_TYPE
) VALUES (
    #{inquiryNo}
  , #{userDto.userNo}
  , #{title}
  , #{content}
  , SYSDATE
  , #{inquiryType}
)

INSERT INTO ANSWER_T (
    INQUIRY_NO
  , CONTENTS
  , CREATED_AT
  , STATUS
) VALUES (
    #{inquiryNo}
  , #{contents}
  , SYSDATE
  , #{status}
)

mapper.select:
SELECT COUNT(*)
  FROM ANSWER_T
 WHERE INQUIRY_NO = #{inquiryNo}

SELECT INQUIRY_NO, CONTENTS, CREATED_AT, STATUS
  FROM ANSWER_T
 WHERE INQUIRY_NO = #{inquiryNo}

