mapper.update:
UPDATE USER_T
   SET NAME = #{name}
     , NICKNAME = #{nickname}
     , GENDER = #{gender}
     , MOBILE = #{mobile}
     , POSTCODE = #{postcode}
     , ROAD_ADDRESS = #{roadAddress}
     , JIBUN_ADDRESS = #{jibunAddress}
     , DETAIL_ADDRESS = #{detailAddress}
     , AGREE = #{agree}
 WHERE USER_NO = #{userNo}

UPDATE USER_T
   SET INTRODUCTION = #{introduction}
 WHERE USER_NO = #{userNo}

UPDATE USER_T
   SET PW = #{pw}
 WHERE USER_NO = #{userNo}

mapper.select:
SELECT COUNT(*)
  FROM DRAW_ORDER_T
 WHERE BUYER_NO = #{buyerNo}
