mapper.select:
SELECT NOTICE_NO, TITLE, CONTENTS, CREATED_AT
  FROM NOTICE_T
 WHERE NOTICE_NO = #{noticeNo}

mapper.insert:

  SELECT NOTICE_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO NOTICE_T (
    NOTICE_NO 
  , TITLE
  , CONTENTS
  , CREATED_AT
) VALUES (
    #{noticeNo}
  , #{title}
  , #{contents}
  , TO_DATE(#{createdAt}, 'YYYY-MM-DD HH24:MI:SS')
)


    SELECT NOTICE_ATTACH_SEQ.NEXTVAL
      FROM DUAL

INSERT INTO NOTICE_ATTACH (
    ATTACH_NO
  , NOTICE_NO
  , PATH
  , FILESYSTEM_NAME
) VALUES (
    #{attachNo}
  , #{noticeNo}
  , #{path}
  , #{filesystemName}
)

mapper.update:
UPDATE NOTICE_T
   SET TITLE = #{title},
       CONTENTS = #{contents}
 WHERE NOTICE_NO = #{noticeNo}
